// Defining variable
var xVal = 0;
var predictions;
var smooth = 15;
var threshold = 50;
var leftreading = [];
var rightreading = [];
var leftavg = [];
var rightavg = [];

// Function to update text input when slider is changed
function updateTextInput(val) {
  document.getElementById('textInput').value = val;
  threshold = val;
}

// Function to change slider value when text input is changed
function updateRangeInput(val) {
  document.getElementById('rangeInput').value = parseInt(val);;
  threshold = val;
}

// Function to print prediction on video feed
function updateCheck(){
  // Get the checkbox
  var checkBox = document.getElementById("myCheck");
  
  // If the checkbox is checked, display the output text
  if (checkBox.checked == true) {
    checkValue = true;
  } else {
    checkValue = false;
  }
}

// Function to smooth an array by a smoothing amount
function smoothing(arr, smooth) {
  return arr.slice(arr.length - smooth, arr.length).reduce((prev, curr) => prev + curr) / smooth
}

// Line chart
var chart = new CanvasJS.Chart("chartContainer", {
  title: {
    text: "Smoothed Distance between Eyelids"
  },
  axisY: {
    minimum: 30,
    maximum: 90
  },
  data: [{
      type: "line",
      dataPoints: leftavg,
      showInLegend: true,
      name: "Left Eye",
    },
    {
      type: "line",
      dataPoints: rightavg,
      showInLegend: true,
      name: "Right Eye",
    }],
  legend: {
    cursor: "pointer",
    verticalAlign: "bottom",
    horizontalAlign: "left",
    dockInsidePlotArea: true,
  }
});

// Base64 encoded string for beep sound
function beep() {
  var snd = new Audio("data:audio/wav;base64,");
  snd.play();
}

// Subrouting to call between predictions and visualization
callProcessing = function(){
  
  // Calculating distances between eyelids
  var points = predictions[0]['mesh']
  lefteye = (distVec(points[385], points[380]) + distVec(points[386], points[374])) / 2
  righteye = (distVec(points[159], points[145]) + distVec(points[158], points[153])) / 2
  
  leftreading.push(lefteye)
  rightreading.push(righteye)
  
  // Pushing new values in array
  if(leftreading.length > smooth) {
    leftavg.push({
      x: xVal,
      y: smoothing(leftreading, smooth)
    })
  }
  if (rightreading.length > smooth) {
    rightavg.push({
      x: xVal,
      y: smoothing(rightreading, smooth)
    })
  }
  xVal++;

  // Shifting graph is the space is full
  if (leftreading.length > 4*smooth || rightreading.length > 4*smooth){
    leftavg.shift();
    rightavg.shift();
  }

  // Calling beep function is both eye distances fall below threshold
  if (leftreading.length > smooth && rightreading.length > smooth) {
    if (leftavg.slice(-1)[0]['y'] < threshold && rightavg.slice(-1)[0]['y'] < threshold) {
      beep();
    }
  }

  // Refreshing chart
  chart.render();
}

// Calling main function
main();
